define([],function(){function createStyleSheet(blocks){var style=document.createElement("style");var text=Object.keys(blocks).map(function(selector){return selector+(" {\n"+processDeclarationBlock(blocks[selector])+"\n}");}).join("\n");style.setAttribute("type","text/css");style.appendChild(document.createTextNode(text));return style;}
function processDeclarationBlock(block){return Object.keys(block).map(function(property){return processDeclaration(property,block[property]);}).join("\n");}
function processDeclaration(property,value){return hyphenate(property)+(": "+value+";");}
function hyphenate(property){return property.replace(/[A-Z]/g,function(match){return"-"+match.toLowerCase();});}
return{createStyleSheet:createStyleSheet};});